var reviews = new GlideRecord('x_484602_netflix_reviews');

reviews.addQuery('state', '3');
reviews.addQuery('filme', current.getValue('filme'));
reviews.query();

var somaDeNotas = 0;
var contador = 0;

while(reviews.next()){
	contador++;
	somaDeNotas += parseInt(reviews.getValue('nota'));
}

var resultado =  somaDeNotas / contador;

var filme = new GlideRecord('x_484602_netflix_filmes');

//nome=Lord of The Rings

filme.addQuery('sys_id', current.getValue('filme'));
filme.query();

if(filme.next()){
	filme.setValue('nota_media', resultado);
	filme.update();
}

