var aprovacao = new GlideRecord("sysapproval_approver");
	aprovacao.addQuery("sysapproval", current.getUniqueValue());
	aprovacao.addQuery("state", "rejected");
	aprovacao.query();

if(aprovacao.next()) {
	current.motivo_reprovacao  = aprovacao.comments.getJournalEntry(1).toString().split("\n")[1];
}

